ymaps.ready(init);

function init() {

    var myMap = new ymaps.Map('map', {
            center: [55.75399400, 37.62209300],
            zoom: 9,
            controls: ['zoomControl'],
            behaviors: ['drag']
        }, {
            searchControlProvider: 'yandex#search'
        }),

        menu = $('<ul class="menu-map"></ul>'),
        menuAdress = $('<ul class="menu-adress"></ul>');
        
    for (var i = 0, l = groups.length; i < l; i++) {
        createMenuGroup(groups[i]);
    }

    for (var i = 0, l = address.length; i < l; i++) {
        createAdressGroup(address[i]);
    }

    function createMenuGroup (group) {

        var menuItem = $('<li><a href="#" class="item-map">' + group.name + '</a></li>'),

            collection = new ymaps.GeoObjectCollection(null, { preset: group.style });

        myMap.geoObjects.add(collection);

        menuItem
            .appendTo(menu)
            .find('a')
            .bind('click', function (e) {
                e.preventDefault();
                myMap.geoObjects.removeAll().add(collection);
                myMap.setCenter([55.75399400, 37.62209300],11);
            });
        for (var j = 0, m = group.items.length; j < m; j++) {
            createSubMenu(group.items[j], collection);
        }
    }

    function createSubMenu (item, collection) {
        var submenuItem = $('<li><a href="#">' + item.name + '</a></li>'),

        placemark = new ymaps.Placemark(item.center, {
            balloonContent: item.name
        }, {
            iconLayout: 'default#image',
            iconImageHref: item.imgPlacemark,
            iconImageSize: [50, 70],
            iconImageOffset: [-25, -70]
        });

        collection.add(placemark);
    }

    function createAdressGroup (addr) {

        titleAdress = $('<span class="title-address">'+ addr.name +'</span>');

        titleAdress.appendTo($('.box-address'));

        collection = new ymaps.GeoObjectCollection(null, { preset: addr.style }),

        myMap.geoObjects.add(collection);

        for (var j = 0, m = addr.items.length; j < m; j++) {
            createMenuAdress(addr.items[j], collection, menuAdress);
        }
    }

    function createMenuAdress (item, collection, menuAdress) {

        var menuItem = $('<li><a href="#" class="item-address">' + item.name + '</a></li>'),

        placemark = new ymaps.Placemark(item.center, {
            balloonContent: item.name
        }, {
            iconLayout: 'default#image',
            iconImageHref: item.imgPlacemark,
            iconImageSize: [50, 70],
            iconImageOffset: [-25, -70]
        });

        collection.add(placemark);

        menuItem
            .appendTo(menuAdress)
            .find('a')
            .bind('click', function (e) {
                e.preventDefault();
                myMap.geoObjects.removeAll().add(placemark);
                myMap.setCenter(placemark.geometry.getCoordinates(),15);
            });
    }

    menu.appendTo($('.block-map'));
    menuAdress.appendTo($('.box-address'));

    $('.item-map').on("click", function(){
        $('.item-map').parent().removeClass("active");
        $(this).parent().addClass("active");
        $('.box-address').removeClass("active");
        $('.item-address').parent().removeClass("active");
    });

    $('.title-address').on("click", function(){
        $('.item-map').parent().removeClass("active");
        if($('.menu-adress').height() == 0){
            $('.box-address').addClass("active");
        } else {
            $('.item-address').parent().removeClass("active");
            $('.box-address').removeClass("active");
            myMap.setCenter([55.75399400, 37.62209300], 11);
            for (var i = 0, l = groups.length; i < l; i++) {
                createMenuGroup(groups[i]);
            }
        }
    });

    $('.item-address').on("click", function(){
        $('.item-address').parent().removeClass("active");
        $(this).parent().addClass("active");
        $('.box-address').removeClass("active");
    });

    //myMap.setBounds(myMap.geoObjects.getBounds());
}